mapper.select:
SELECT b.*, c.name AS category_name, bs.floor, bs.area, bs.frame, (SELECT COUNT(bsave.id) FROM book_save bsave WHERE bsave.book_id = b.id AND bsave.user_id = #{userId}) AS is_save, (SELECT boh.is_return FROM book_order_history boh WHERE boh.book_id = b.id AND boh.user_id = #{userId} ORDER BY boh.id DESC LIMIT 1) AS is_return, (SELECT COUNT(brh.id) FROM book_rss_history brh WHERE brh.book_id = b.id AND brh.user_id = #{userId}) AS is_rss FROM book b LEFT JOIN category c ON c.id = b.category_id LEFT JOIN bookshelf bs ON bs.id = b.bookshelf_id
AND b.id = #{id}
AND b.name LIKE concat('%',#{name},'%')
AND b.publisher LIKE concat('%',#{publisher},'%')
AND b.category_id = #{categoryId}
AND b.is_plan_buy = #{isPlanBuy}
AND b.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY b.create_time DESC
LIMIT #{current},#{size}

SELECT COUNT(*)
FROM book b
LEFT JOIN category c ON c.id = b.category_id
LEFT JOIN bookshelf bs ON bs.id = b.bookshelf_id

    
        AND b.id = #{id}
    
    
